		private void AddToRefreshList(List<Category> refreshCategories, List<CategoryProduct> refreshCategoryProducts)
		{
			if (IsDirty)
				refreshCategories.Add(this);
			if (_CategoryProducts != null && _CategoryProducts.IsDirty)
			{
				foreach (CategoryProduct tmp in _CategoryProducts)
				{
					if(tmp.IsDirty)refreshCategoryProducts.Add(tmp);
				}
			}
		}
		private void BuildRefreshList()
		{
			_RefreshCategories = new List<Category>();
			_RefreshCategoryProducts = new List<CategoryProduct>();
			AddToRefreshList(_RefreshCategories, _RefreshCategoryProducts);
		}
		private void ProcessRefreshList()
		{
			foreach (Category tmp in _RefreshCategories)
			{
				CategoryInfo.Refresh(tmp);
			}
			foreach (CategoryProduct tmp in _RefreshCategoryProducts)
			{
				ProductInfo.Refresh(tmp);
			}
		}
