		private static void ConvertListToDictionary()
		{
			List<Category> remove = new List<Category>();
			foreach (Category tmp in _AllList)
			{
				_AllByPrimaryKey[tmp.CategoryID.ToString()]=tmp; // Primary Key
				remove.Add(tmp);
			}
			foreach (Category tmp in remove)
				_AllList.Remove(tmp);
		}
		public static Category GetExistingByPrimaryKey(int categoryID)
		{
			ConvertListToDictionary();
			string key = categoryID.ToString();
			if (_AllByPrimaryKey.ContainsKey(key)) return _AllByPrimaryKey[key]; 
			return null;
		}
