			tmp.Picture = picture;
			return tmp;
		}
		public static Category MakeCategory(string categoryName, string description, byte[] picture)
		{
			Category tmp = Category.New(categoryName, description, picture);
			if (tmp.IsSavable)
				tmp = tmp.Save();
			else
			{
				Csla.Validation.BrokenRulesCollection brc = tmp.ValidationRules.GetBrokenRules();
				tmp._ErrorMessage = "Failed Validation:";
				foreach (Csla.Validation.BrokenRule br in brc)
				{
					tmp._ErrorMessage += "\r\n\tFailure: " + br.RuleName;
				}
			}
			return tmp;
		}
		public static Category Get(int categoryID)
		{
			if (!CanGetObject())
				throw new System.Security.SecurityException("User not authorized to view a Category");
			try
			{
				Category tmp = GetExistingByPrimaryKey(categoryID);
				if (tmp == null)
				{
					tmp = DataPortal.Fetch<Category>(new PKCriteria(categoryID));
					_AllList.Add(tmp);
				}
				if (tmp.ErrorMessage == "No Record Found") tmp = null;
				return tmp;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on Category.Get", ex);
			}
		}
