		public override Category Save()
		{
			if (IsDeleted && !CanDeleteObject())
				throw new System.Security.SecurityException("User not authorized to remove a Category");
			else if (IsNew && !CanAddObject())
				throw new System.Security.SecurityException("User not authorized to add a Category");
			else if (!CanEditObject())
				throw new System.Security.SecurityException("User not authorized to update a Category");
			try
			{
				BuildRefreshList();
				Category category = base.Save();
				_AllList.Add(category);//Refresh the item in AllList
				ProcessRefreshList();
				return category;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on CSLA Save", ex);
			}
		}
