		private void ReadData(SafeDataReader dr)
		{
			Database.LogInfo("Category.ReadData", GetHashCode());
			try
			{
				_CategoryID = dr.GetInt32("CategoryID");
				_CategoryName = dr.GetString("CategoryName");
				_Description = dr.GetString("Description");
				_Picture = (byte[])dr.GetValue("Picture");
				_CategoryProductCount = dr.GetInt32("ProductCount");
				MarkOld();
			}
			catch (Exception ex)
			{
				Database.LogException("Category.ReadData", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("Category.ReadData", ex);
			}
		}
		private void DataPortal_Fetch(PKCriteria criteria)
		{
			Database.LogInfo("Category.DataPortal_Fetch", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					ApplicationContext.LocalContext["cn"] = cn;
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getCategory";
						cm.Parameters.AddWithValue("@CategoryID", criteria.CategoryID);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							if (!dr.Read())
							{
								_ErrorMessage = "No Record Found";
								return;
							}
							ReadData(dr);
							// load child objects
							dr.NextResult();
							_CategoryProducts = CategoryProducts.Get(dr);
						}
					}
					// removing of item only needed for local data portal
					if (ApplicationContext.ExecutionLocation == ApplicationContext.ExecutionLocations.Client)
						ApplicationContext.LocalContext.Remove("cn");
				}
			}
			catch (Exception ex)
			{
				Database.LogException("Category.DataPortal_Fetch", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("Category.DataPortal_Fetch", ex);
			}
		}
