		#region Data Access Portal
		// called to load data from the database
		private void Fetch(SafeDataReader dr, Employee parent)
		{
			this.RaiseListChangedEvents = false;
			while (dr.Read())
				this.Add(Employee.Get(dr, parent));
			this.RaiseListChangedEvents = true;
		}
		[Serializable()]
		private class ReportsToCriteria
		{
			public ReportsToCriteria(int? reportsTo)
			{
				_ReportsTo = reportsTo;
			}
			private int? _ReportsTo;
			public int? ReportsTo
			{
				get { return _ReportsTo; }
				set { _ReportsTo = value; }
			}
		}
		private void DataPortal_Fetch(ReportsToCriteria criteria)
		{
			this.RaiseListChangedEvents = false;
			Database.LogInfo("ChildEmployees.DataPortal_FetchReportsTo", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getChildEmployees";
						cm.Parameters.AddWithValue("@ReportsTo", criteria.ReportsTo);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							while (dr.Read()) this.Add(new Employee(dr));
						}
					}
				}
			}
			catch (Exception ex)
			{
				Database.LogException("ChildEmployees.DataPortal_FetchReportsTo", ex);
				throw new DbCslaException("ChildEmployees.DataPortal_Fetch", ex);
			}
			this.RaiseListChangedEvents = true;
		}
		internal void Update(Employee employee)
		{
			this.RaiseListChangedEvents = false;
			try
			{
				// update (thus deleting) any deleted child objects
				foreach (Employee obj in DeletedList)
					obj.Delete();// TODO: Should this be SQLDelete
				// now that they are deleted, remove them from memory too
				DeletedList.Clear();
				// add/update any current child objects
				foreach (Employee obj in this)
				{
					if (obj.IsNew)
						obj.SQLInsert();
					else
						obj.SQLUpdate();
				}
			}
			finally
			{
				this.RaiseListChangedEvents = true;
			}
		}
