		private void DataPortal_Fetch(CustomerIDCriteria criteria)
		{
			this.RaiseListChangedEvents = false;
			Database.LogInfo("CustomerOrders.DataPortal_FetchCustomerID", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getOrdersByCustomerID";
						cm.Parameters.AddWithValue("@CustomerID", criteria.CustomerID);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							while (dr.Read()) this.Add(new CustomerOrder(dr));
						}
					}
				}
			}
			catch (Exception ex)
			{
				Database.LogException("CustomerOrders.DataPortal_FetchCustomerID", ex);
				throw new DbCslaException("CustomerOrders.DataPortal_Fetch", ex);
			}
			this.RaiseListChangedEvents = true;
		}
		internal void Update(Customer customer)
		{
			this.RaiseListChangedEvents = false;
			try
			{
				// update (thus deleting) any deleted child objects
				foreach (CustomerOrder obj in DeletedList)
					obj.Delete();// TODO: Should this be SQLDelete
				// now that they are deleted, remove them from memory too
				DeletedList.Clear();
				// add/update any current child objects
				foreach (CustomerOrder obj in this)
				{
					if (obj.IsNew)
						obj.Insert(customer);
					else
						obj.Update(customer);
				}
			}
			finally
			{
				this.RaiseListChangedEvents = true;
			}
		}
