	public delegate void RegionInfoEvent(object sender);
	/// <summary>
	///	RegionInfo Generated by MyGeneration using the CSLA Object Mapping template
	/// </summary>
	[Serializable()]
	[TypeConverter(typeof(RegionInfoConverter))]
	public partial class RegionInfo : ReadOnlyBase<RegionInfo>, IDisposable
	{
		public event RegionInfoEvent Changed;
		private void OnChange()
		{
			if (Changed != null) Changed(this);
		}
		#region Collection
		protected static List<RegionInfo> _AllList = new List<RegionInfo>();
		private static Dictionary<string, RegionInfo> _AllByPrimaryKey = new Dictionary<string, RegionInfo>();
		private static void ConvertListToDictionary()
		{
			List<RegionInfo> remove = new List<RegionInfo>();
			foreach (RegionInfo tmp in _AllList)
			{
				_AllByPrimaryKey[tmp.RegionID.ToString()]=tmp; // Primary Key
				remove.Add(tmp);
			}
			foreach (RegionInfo tmp in remove)
				_AllList.Remove(tmp);
		}
		internal static void AddList(RegionInfoList lst)
		{
			foreach (RegionInfo item in lst) _AllList.Add(item);
		}
		public static RegionInfo GetExistingByPrimaryKey(int regionID)
		{
			ConvertListToDictionary();
			string key = regionID.ToString();
			if (_AllByPrimaryKey.ContainsKey(key)) return _AllByPrimaryKey[key]; 
			return null;
		}
		#endregion
		#region Business Methods
		private string _ErrorMessage = string.Empty;
		public string ErrorMessage
		{
			get { return _ErrorMessage; }
		}
		protected Region _Editable;
		private IVEHasBrokenRules HasBrokenRules
		{
			get
			{
				IVEHasBrokenRules hasBrokenRules = null;
				if (_Editable != null)
					hasBrokenRules = _Editable.HasBrokenRules;
				return hasBrokenRules;
			}
		}
		private int _RegionID;
		[System.ComponentModel.DataObjectField(true, true)]
		public int RegionID
		{
			[System.Runtime.CompilerServices.MethodImpl(System.Runtime.CompilerServices.MethodImplOptions.NoInlining)]
			get
			{
				CanReadProperty(true);
				return _RegionID;
			}
		}
		private string _RegionDescription = string.Empty;
		public string RegionDescription
		{
			[System.Runtime.CompilerServices.MethodImpl(System.Runtime.CompilerServices.MethodImplOptions.NoInlining)]
			get
			{
				CanReadProperty(true);
				return _RegionDescription;
			}
		}
		private int _RegionTerritoryCount = 0;
		/// <summary>
		/// Count of RegionTerritories for this Region
		/// </summary>
		public int RegionTerritoryCount
		{
			[System.Runtime.CompilerServices.MethodImpl(System.Runtime.CompilerServices.MethodImplOptions.NoInlining)]
			get
			{
				CanReadProperty(true);
				return _RegionTerritoryCount;
			}
		}
		private TerritoryInfoList _RegionTerritories = null;
		[TypeConverter(typeof(TerritoryInfoListConverter))]
		public TerritoryInfoList RegionTerritories
		{
			[System.Runtime.CompilerServices.MethodImpl(System.Runtime.CompilerServices.MethodImplOptions.NoInlining)]
			get
			{
				CanReadProperty(true);
				if (_RegionTerritoryCount > 0 && _RegionTerritories == null)
					_RegionTerritories = TerritoryInfoList.GetByRegionID(_RegionID);
				return _RegionTerritories;
			}
		}
		// TODO: Replace base RegionInfo.ToString function as necessary
		/// <summary>
