		/// <summary>
		/// Overrides Base GetIdValue - Used internally by CSLA to determine equality
		/// </summary>
		/// <returns>A Unique ID for the current RegionInfo</returns>
		protected override object GetIdValue()
		{
			return _RegionID;
		}
		#endregion
		#region Factory Methods
		private RegionInfo()
		{/* require use of factory methods */
			_AllList.Add(this);
		}
		public void Dispose()
		{
			_AllList.Remove(this);
			_AllByPrimaryKey.Remove(RegionID.ToString());
		}
		public virtual Region Get()
		{
			return _Editable = Region.Get(_RegionID);
		}
		public static void Refresh(Region tmp)
		{
			RegionInfo tmpInfo = GetExistingByPrimaryKey(tmp.RegionID);
			if (tmpInfo == null) return;
			tmpInfo.RefreshFields(tmp);
		}
		private void RefreshFields(Region tmp)
		{
			_RegionDescription = tmp.RegionDescription;
			_RegionInfoExtension.Refresh(this);
			OnChange();// raise an event
		}
		public static RegionInfo Get(int regionID)
		{
			//if (!CanGetObject())
			//  throw new System.Security.SecurityException("User not authorized to view a Region");
			try
			{
				RegionInfo tmp = GetExistingByPrimaryKey(regionID);
				if (tmp == null)
				{
					tmp = DataPortal.Fetch<RegionInfo>(new PKCriteria(regionID));
					_AllList.Add(tmp);
				}
				if (tmp.ErrorMessage == "No Record Found") tmp = null;
				return tmp;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on RegionInfo.Get", ex);
			}
		}
		#endregion
		#region Data Access Portal
		internal RegionInfo(SafeDataReader dr)
		{
			Database.LogInfo("RegionInfo.Constructor", GetHashCode());
			try
			{
				ReadData(dr);
			}
			catch (Exception ex)
			{
				Database.LogException("RegionInfo.Constructor", ex);
				throw new DbCslaException("RegionInfo.Constructor", ex);
			}
		}
		[Serializable()]
		protected class PKCriteria
		{
			private int _RegionID;
			public int RegionID
			{ get { return _RegionID; } }
			public PKCriteria(int regionID)
			{
				_RegionID = regionID;
			}
		}
		private void ReadData(SafeDataReader dr)
		{
			Database.LogInfo("RegionInfo.ReadData", GetHashCode());
			try
			{
				_RegionID = dr.GetInt32("RegionID");
				_RegionDescription = dr.GetString("RegionDescription");
				_RegionTerritoryCount = dr.GetInt32("TerritoryCount");
			}
			catch (Exception ex)
			{
				Database.LogException("RegionInfo.ReadData", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("RegionInfo.ReadData", ex);
			}
		}
		private void DataPortal_Fetch(PKCriteria criteria)
		{
			Database.LogInfo("RegionInfo.DataPortal_Fetch", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					ApplicationContext.LocalContext["cn"] = cn;
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getRegion";
						cm.Parameters.AddWithValue("@RegionID", criteria.RegionID);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							if (!dr.Read())
							{
								_ErrorMessage = "No Record Found";
								return;
							}
							ReadData(dr);
						}
					}
					// removing of item only needed for local data portal
					if (ApplicationContext.ExecutionLocation == ApplicationContext.ExecutionLocations.Client)
						ApplicationContext.LocalContext.Remove("cn");
				}
			}
			catch (Exception ex)
			{
				Database.LogException("RegionInfo.DataPortal_Fetch", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("RegionInfo.DataPortal_Fetch", ex);
			}
		}
