		private List<SupplierProduct> _RefreshSupplierProducts = new List<SupplierProduct>();
		private void AddToRefreshList(List<Supplier> refreshSuppliers, List<SupplierProduct> refreshSupplierProducts)
		{
			if (IsDirty)
				refreshSuppliers.Add(this);
			if (_SupplierProducts != null && _SupplierProducts.IsDirty)
			{
				foreach (SupplierProduct tmp in _SupplierProducts)
				{
					if(tmp.IsDirty)refreshSupplierProducts.Add(tmp);
				}
			}
		}
		private void BuildRefreshList()
		{
			_RefreshSuppliers = new List<Supplier>();
			_RefreshSupplierProducts = new List<SupplierProduct>();
			AddToRefreshList(_RefreshSuppliers, _RefreshSupplierProducts);
		}
		private void ProcessRefreshList()
		{
			foreach (Supplier tmp in _RefreshSuppliers)
			{
				SupplierInfo.Refresh(tmp);
			}
			foreach (SupplierProduct tmp in _RefreshSupplierProducts)
			{
				ProductInfo.Refresh(tmp);
			}
		}
