		#region Collection
		protected static List<Supplier> _AllList = new List<Supplier>();
		private static Dictionary<string, Supplier> _AllByPrimaryKey = new Dictionary<string, Supplier>();
		private static void ConvertListToDictionary()
		{
			List<Supplier> remove = new List<Supplier>();
			foreach (Supplier tmp in _AllList)
			{
				_AllByPrimaryKey[tmp.SupplierID.ToString()]=tmp; // Primary Key
				remove.Add(tmp);
			}
			foreach (Supplier tmp in remove)
				_AllList.Remove(tmp);
		}
		public static Supplier GetExistingByPrimaryKey(int supplierID)
		{
			ConvertListToDictionary();
			string key = supplierID.ToString();
			if (_AllByPrimaryKey.ContainsKey(key)) return _AllByPrimaryKey[key]; 
			return null;
		}
