		public static Supplier New()
		{
			if (!CanAddObject())
				throw new System.Security.SecurityException("User not authorized to add a Supplier");
			try
			{
				return DataPortal.Create<Supplier>();
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on Supplier.New", ex);
			}
		}
		public static Supplier New(string companyName)
		{
			Supplier tmp = Supplier.New();
			tmp.CompanyName = companyName;
			return tmp;
		}
		public static Supplier New(string companyName, string contactName, string contactTitle, string address, string city, string region, string postalCode, string country, string phone, string fax, string homePage)
		{
			Supplier tmp = Supplier.New();
			tmp.CompanyName = companyName;
			tmp.ContactName = contactName;
			tmp.ContactTitle = contactTitle;
			tmp.Address = address;
			tmp.City = city;
			tmp.Region = region;
			tmp.PostalCode = postalCode;
			tmp.Country = country;
			tmp.Phone = phone;
			tmp.Fax = fax;
			tmp.HomePage = homePage;
			return tmp;
		}
		public static Supplier MakeSupplier(string companyName, string contactName, string contactTitle, string address, string city, string region, string postalCode, string country, string phone, string fax, string homePage)
		{
			Supplier tmp = Supplier.New(companyName, contactName, contactTitle, address, city, region, postalCode, country, phone, fax, homePage);
			if (tmp.IsSavable)
				tmp = tmp.Save();
			else
			{
				Csla.Validation.BrokenRulesCollection brc = tmp.ValidationRules.GetBrokenRules();
				tmp._ErrorMessage = "Failed Validation:";
				foreach (Csla.Validation.BrokenRule br in brc)
				{
					tmp._ErrorMessage += "\r\n\tFailure: " + br.RuleName;
				}
			}
			return tmp;
		}
		public static Supplier Get(int supplierID)
		{
			if (!CanGetObject())
				throw new System.Security.SecurityException("User not authorized to view a Supplier");
			try
			{
				Supplier tmp = GetExistingByPrimaryKey(supplierID);
				if (tmp == null)
				{
					tmp = DataPortal.Fetch<Supplier>(new PKCriteria(supplierID));
					_AllList.Add(tmp);
				}
				if (tmp.ErrorMessage == "No Record Found") tmp = null;
				return tmp;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on Supplier.Get", ex);
			}
		}
