		public static void Delete(int supplierID)
		{
			if (!CanDeleteObject())
				throw new System.Security.SecurityException("User not authorized to remove a Supplier");
			try
			{
				DataPortal.Delete(new PKCriteria(supplierID));
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on Supplier.Delete", ex);
			}
		}
		public override Supplier Save()
		{
			if (IsDeleted && !CanDeleteObject())
				throw new System.Security.SecurityException("User not authorized to remove a Supplier");
			else if (IsNew && !CanAddObject())
				throw new System.Security.SecurityException("User not authorized to add a Supplier");
			else if (!CanEditObject())
				throw new System.Security.SecurityException("User not authorized to update a Supplier");
			try
			{
				BuildRefreshList();
				Supplier supplier = base.Save();
				_AllList.Add(supplier);//Refresh the item in AllList
				ProcessRefreshList();
				return supplier;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on CSLA Save", ex);
			}
		}
