		private static void ConvertListToDictionary()
		{
			List<TerritoryInfo> remove = new List<TerritoryInfo>();
			foreach (TerritoryInfo tmp in _AllList)
			{
				_AllByPrimaryKey[tmp.TerritoryID.ToString()]=tmp; // Primary Key
				remove.Add(tmp);
			}
			foreach (TerritoryInfo tmp in remove)
				_AllList.Remove(tmp);
		}
