		}
		public static TerritoryInfo GetExistingByPrimaryKey(string territoryID)
		{
			ConvertListToDictionary();
			string key = territoryID.ToString();
			if (_AllByPrimaryKey.ContainsKey(key)) return _AllByPrimaryKey[key]; 
			return null;
		}
