		public static void Refresh(Territory tmp)
		{
			TerritoryInfo tmpInfo = GetExistingByPrimaryKey(tmp.TerritoryID);
			if (tmpInfo == null) return;
			tmpInfo.RefreshFields(tmp);
		}
		private void RefreshFields(Territory tmp)
