		public static TerritoryInfo Get(string territoryID)
		{
			//if (!CanGetObject())
			//  throw new System.Security.SecurityException("User not authorized to view a Territory");
			try
			{
				TerritoryInfo tmp = GetExistingByPrimaryKey(territoryID);
				if (tmp == null)
				{
					tmp = DataPortal.Fetch<TerritoryInfo>(new PKCriteria(territoryID));
					_AllList.Add(tmp);
				}
				if (tmp.ErrorMessage == "No Record Found") tmp = null;
				return tmp;
			}
			catch (Exception ex)
			{
				throw new DbCslaException("Error on TerritoryInfo.Get", ex);
			}
		}
