		[Serializable()]
		protected class PKCriteria
		{
			private string _TerritoryID;
			public string TerritoryID
			{ get { return _TerritoryID; } }
			public PKCriteria(string territoryID)
			{
				_TerritoryID = territoryID;
			}
		}
		private void ReadData(SafeDataReader dr)
		{
			Database.LogInfo("TerritoryInfo.ReadData", GetHashCode());
			try
			{
				_TerritoryID = dr.GetString("TerritoryID");
				_TerritoryDescription = dr.GetString("TerritoryDescription");
				_RegionID = dr.GetInt32("RegionID");
				_TerritoryEmployeeTerritoryCount = dr.GetInt32("EmployeeTerritoryCount");
			}
			catch (Exception ex)
			{
				Database.LogException("TerritoryInfo.ReadData", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("TerritoryInfo.ReadData", ex);
			}
		}
		private void DataPortal_Fetch(PKCriteria criteria)
		{
			Database.LogInfo("TerritoryInfo.DataPortal_Fetch", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					ApplicationContext.LocalContext["cn"] = cn;
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getTerritory";
						cm.Parameters.AddWithValue("@TerritoryID", criteria.TerritoryID);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							if (!dr.Read())
							{
								_ErrorMessage = "No Record Found";
								return;
							}
							ReadData(dr);
						}
					}
					// removing of item only needed for local data portal
					if (ApplicationContext.ExecutionLocation == ApplicationContext.ExecutionLocations.Client)
						ApplicationContext.LocalContext.Remove("cn");
				}
			}
			catch (Exception ex)
			{
				Database.LogException("TerritoryInfo.DataPortal_Fetch", ex);
				_ErrorMessage = ex.Message;
				throw new DbCslaException("TerritoryInfo.DataPortal_Fetch", ex);
			}
		}
