		private void DataPortal_Fetch(SupplierIDCriteria criteria)
		{
			this.RaiseListChangedEvents = false;
			Database.LogInfo("SupplierProducts.DataPortal_FetchSupplierID", GetHashCode());
			try
			{
				using (SqlConnection cn = Database.Northwind_SqlConnection)
				{
					using (SqlCommand cm = cn.CreateCommand())
					{
						cm.CommandType = CommandType.StoredProcedure;
						cm.CommandText = "getProductsBySupplierID";
						cm.Parameters.AddWithValue("@SupplierID", criteria.SupplierID);
						using (SafeDataReader dr = new SafeDataReader(cm.ExecuteReader()))
						{
							while (dr.Read()) this.Add(new SupplierProduct(dr));
						}
					}
				}
			}
			catch (Exception ex)
			{
				Database.LogException("SupplierProducts.DataPortal_FetchSupplierID", ex);
				throw new DbCslaException("SupplierProducts.DataPortal_Fetch", ex);
			}
			this.RaiseListChangedEvents = true;
		}
		internal void Update(Supplier supplier)
		{
			this.RaiseListChangedEvents = false;
			try
			{
				// update (thus deleting) any deleted child objects
				foreach (SupplierProduct obj in DeletedList)
					obj.Delete();// TODO: Should this be SQLDelete
				// now that they are deleted, remove them from memory too
				DeletedList.Clear();
				// add/update any current child objects
				foreach (SupplierProduct obj in this)
				{
					if (obj.IsNew)
						obj.Insert(supplier);
					else
						obj.Update(supplier);
				}
			}
			finally
			{
				this.RaiseListChangedEvents = true;
			}
		}
